import React, { useState } from "react";
import * as Yup from "yup";
import { Form, Input } from "@rocketseat/unform";
import Axios from "axios";

import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import "./index.css";
import banner from "../../assets/banner.jpg";

export default function Cardd() {
  const [values, setValues] = useState("");

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }
  async function onSubmit() {
    await Axios.post("http://mockbin.com/request?foo=bar&foo=baz", values).then(
      (response) => {
        console.log(response);
        if (response.status === 200) {
          alert("Mensagem enviada com sucesso!");
        } else {
          alert("Erro ao enviar mensagem.");
        }
      }
    );
  }

  return (
    <div>
      <Card>
        <Card.Img src={banner} alt='Card image' className='banner' />
        <Card.ImgOverlay>
          <div className='texto'>
            <h1>Entregando cargas e sonhos</h1>
            <p>
              Desde 1980 entregando cargas e fazemos pessoas negocios sorriem e
              se realizarem
            </p>
          </div>
        </Card.ImgOverlay>
      </Card>
    </div>
  );
}
